-- This method is called by the pipeline during initializationfunction Initialize ()    local areaName = "Zone"    local layerName = areaName .. " Layer"    local currentNodes = 6    local targetNodes = 10            local graph = GraphBuilder.BuildCircleGraph("Pocket World", currentNodes, layerName, areaName .. " {0}")    local areaLayer = graph:GetLayer(layerName)        while currentNodes < targetNodes do        local relation = areaLayer:GetSomewhatRandomRelationWithDegreeSmaller(4)        local newEntity = Entity(areaName .. " " .. tostring(currentNodes+1), graph)        graph.Entities:Add(newEntity)        areaLayer:AddRelation(relation.Nodes[0], newEntity, areaName .. " Connection '" .. relation.Nodes[0].Name .. "' <-> '" .. newEntity.Name .. "'")        areaLayer:AddRelation(newEntity, relation.Nodes[1], areaName .. " Connection '" .. newEntity.Name .. "' <-> '" .. relation.Nodes[1].Name .. "'")                currentNodes = currentNodes + 1    end        return graphend-- This method is called by the pipeline in a stepfunction ExecuteStep (graph)    local entity = Entity('Lua Test Node', graph)    graph.Entities:Add(entity)end